var slider = $('#price-range').noUiSlider({
		start: [ 0, 500 ],
		connect: true,
		range: {
			'min': 0,
			'max': 500
		}
	});
	$('#price-range').Link('lower').to($('#range-number-min'), null, wNumb({
		decimals: 0
	}));
